('sys','optimizer_switch_choice','PROCEDURE','optimizer_switch_choice','SQL','CONTAINS_SQL','NO','INVOKER','IN on_off VARCHAR(3)','','BEGIN\n  DECLARE tmp VARCHAR(1024);\n  DECLARE opt VARCHAR(1024);\n  DECLARE start INT;\n  DECLARE end INT;\n  DECLARE pos INT;\n  set tmp=concat(@@optimizer_switch,\",\");\n  CREATE OR REPLACE TEMPORARY TABLE tmp_opt_switch (a varchar(64), opt CHAR(3)) character set latin1 engine=heap;\n  set start=1;\n  FIND_OPTIONS:\n  LOOP\n    set pos= INSTR(SUBSTR(tmp, start), \",\");\n    if (pos = 0) THEN\n       LEAVE FIND_OPTIONS;\n    END IF;\n    set opt= MID(tmp, start, pos-1);\n    set end= INSTR(opt, \"=\");\n    insert into tmp_opt_switch values(LEFT(opt,end-1),SUBSTR(opt,end+1));\n    set start=start + pos;\n  END LOOP;\n  SELECT  t.a as \"option\",t.opt from tmp_opt_switch as t where t.opt = on_off order by a;\n  DROP TEMPORARY TABLE tmp_opt_switch;\nEND','mariadb.sys@localhost','2024-11-20 16:41:53','2024-11-20 16:41:53','','return @@optimizer_switch options as a result set for easier readability','utf8mb3','utf8mb3_general_ci','utf8mb3_general_ci','BEGIN\n  DECLARE tmp VARCHAR(1024);\n  DECLARE opt VARCHAR(1024);\n  DECLARE start INT;\n  DECLARE end INT;\n  DECLARE pos INT;\n  set tmp=concat(@@optimizer_switch,\",\");\n  CREATE OR REPLACE TEMPORARY TABLE tmp_opt_switch (a varchar(64), opt CHAR(3)) character set latin1 engine=heap;\n  set start=1;\n  FIND_OPTIONS:\n  LOOP\n    set pos= INSTR(SUBSTR(tmp, start), \",\");\n    if (pos = 0) THEN\n       LEAVE FIND_OPTIONS;\n    END IF;\n    set opt= MID(tmp, start, pos-1);\n    set end= INSTR(opt, \"=\");\n    insert into tmp_opt_switch values(LEFT(opt,end-1),SUBSTR(opt,end+1));\n    set start=start + pos;\n  END LOOP;\n  SELECT  t.a as \"option\",t.opt from tmp_opt_switch as t where t.opt = on_off order by a;\n  DROP TEMPORARY TABLE tmp_opt_switch;\nEND','NONE'),
